window.onload=init;const choreContainer=document.getElementById("entries"),allEntries=document.getElementsByClassName("singleChore"),entryList=document.getElementById("entries").getElementsByTagName("input"),checkedAttribs=[],classHidden=[];function init(){choreContainer.innerHTML=localStorage.getItem("previousState"),restoreChkbxStatus(entryList.length,0),showRemoveBtn()}function addEntry(e){e.preventDefault();const t=choreContainer.innerHTML,n=document.getElementById("chore").value;if(""!=n){checkState();const e='<li class = "singleChore"><input type="checkbox"><span>'+n+'</span></input><span class = "delete-single-chore" onclick = "removeOne(event)" >&times;</span></li>';choreContainer.innerHTML=e+t,restoreChkbxStatus(entryList.length-1,1),checkState(),document.getElementById("chore").value="",localStorage.setItem("previousState",choreContainer.innerHTML),showRemoveBtn()}else alert("You have not entered anything.")}function checkState(){for(var e=0;e<entryList.length;e++)checkedAttribs[e]=entryList[e].checked,classHidden[e]=entryList[e].parentNode.style.display;console.log(classHidden),localStorage.setItem("checkboxStatuses",JSON.stringify(checkedAttribs)),localStorage.setItem("displayStatuses",JSON.stringify(classHidden))}function restoreChkbxStatus(e,t){const n=JSON.parse(localStorage.getItem("checkboxStatuses")),o=JSON.parse(localStorage.getItem("displayStatuses"));for(var s=0;s<e;s++)entryList[s+t].checked=n[s],entryList[s+t].parentNode.style.display=o[s]}function removeOne(e){e.target.parentNode.style.display="none"}function removeAll(){localStorage.clear(),window.location.reload()}function showRemoveBtn(){document.getElementById("entries").childElementCount>1&&(document.getElementById("removeBtn").style.display="initial")}