window.onload=init;const allEntries=document.getElementById("entries"),entryList=document.getElementById("entries").getElementsByTagName("input"),checkedAttribs=[];function init(){allEntries.innerHTML=localStorage.getItem("previousState"),restoreChkbxStatus(entryList.length,0),showRemoveBtn()}function addEntry(e){e.preventDefault();const t=allEntries.innerHTML,n=document.getElementById("chore").value;if(""!=n){checkState();const e='<li class = "allEntries"><input type="checkbox"><span>'+n+'</span></input><span class = "delete-single-chore" onclick = " this.parentNode.style.display = \'none\' ">&times;</span></li>';allEntries.innerHTML=e+t,restoreChkbxStatus(entryList.length-1,1),checkState(),document.getElementById("chore").value="",localStorage.setItem("previousState",allEntries.innerHTML),showRemoveBtn()}else alert("You have not entered anything.")}function checkState(){for(var e=0;e<entryList.length;e++)checkedAttribs[e]=entryList[e].checked;localStorage.setItem("checkboxStatuses",JSON.stringify(checkedAttribs))}function restoreChkbxStatus(e,t){const n=JSON.parse(localStorage.getItem("checkboxStatuses"));for(var o=0;o<e;o++)entryList[o+t].checked=n[o]}function removeOne(e){console.log(e.target),console.log(e.currentTarget)}function removeAll(){localStorage.clear(),window.location.reload()}function showRemoveBtn(){document.getElementById("entries").childElementCount>1&&(document.getElementById("removeBtn").style.display="initial")}